const data = JSON.parse(eventsFunctionContext.getArgument("data"));
const branches = data.branches;
const num = Object.keys(branches).length;

const resolution_width = runtimeScene.getGame().getGameResolutionWidth();
const resolution_height = runtimeScene.getGame().getGameResolutionHeight();
const branch_width = resolution_width / 10;
const branch_height = Math.min(resolution_height / num, resolution_height / 10);
const x_offset = 50;
const y_offset = 150;
const y_gap = (resolution_height - y_offset) / num;
const branch_logo_name = "branch_logo";
const branch_value_name = "branch_value";
const branch_num_mozcoins_txt_name = "branch_num_mozcoins_txt";
const branch_num_mozcoins_capture_name = "branch_num_mozcoins_capture";

let counter = 0;
branches.forEach((value, _) => {
    // create new branch logo
    const new_branch = runtimeScene.createObject(branch_logo_name);
    new_branch.setHeight(branch_height);
    new_branch.setWidth(branch_width);
    new_branch.setX(resolution_width - branch_width - x_offset);
    new_branch.setY(y_offset + counter * y_gap);
    new_branch.setZOrder(2);

    // create new branch value
    const new_value = runtimeScene.createObject(branch_value_name);
    new_value.setHeight(10);
    new_value.setWidth(20);
    new_value.setX(resolution_width - branch_width - x_offset);
    new_value.setY(y_offset + counter * y_gap);
    new_value.setZOrder(2);
    new_value.setString(value);

    // create input capture
    const capture = runtimeScene.createObject(branch_num_mozcoins_capture_name);
    capture.activate(false);
    capture.setHeight(0);
    capture.setWidth(0);
    capture.setX(0);
    capture.setY(0);
    capture.setZOrder(2);

    // create input txt
    const txt = runtimeScene.createObject(branch_num_mozcoins_txt_name);
    txt.setHeight(10);
    txt.setWidth(20);
    txt.setX(resolution_width - branch_width / 2 - x_offset);
    txt.setY(y_offset + counter * y_gap);
    txt.setZOrder(2);

    counter++;
});

const num_mozcoins = runtimeScene.getGame().getVariables().get("user").getChild("num_mozcoins").getAsNumber();
const branch_num_mozcoins_capture_name = "branch_num_mozcoins_capture";
const branch_num_mozcoins_txt_name = "branch_num_mozcoins_txt";
const branch_logo_name = "branch_logo";
const branch_logos = runtimeScene.getObjects(branch_logo_name);
const branch_txts = runtimeScene.getObjects(branch_num_mozcoins_txt_name);
const branch_captures = runtimeScene.getObjects(branch_num_mozcoins_capture_name);
const numbers = /([0-9]+)/;
// TODO
const branch_num_animation = 3;

// TODO maybe the txt is not corresponds to capture or branch
let c = 0;
let input;
let consumed_moz = 0;
let current_txt = null;
let current_capture = null;
let current_branch = null;

for(let txt of branch_txts) {
    if (branch_captures[c].isActivated()) {
        current_txt = txt;
        current_capture = branch_captures[c];
        current_branch = branch_logos[c];
    } else {
        input = branch_captures[c].getString().replace(/\D/g, '');
        input = input.substring(0, Math.min(input.length, 3));

        if (input === "") {
            input = "0";
        }

        consumed_moz += parseInt(input);
        branch_captures[c].setString(input);
        txt.setString(input);

        branch_logos[c].setAnimation(Math.floor(parseInt(input) / (num_mozcoins / branch_num_animation)));
    }
    c++;
}

if (current_txt === null) {
    return;
}

input = current_capture.getString().replace(/\D/g, '');
input = input.substring(0, Math.min(input.length, 3));

if (input === "") {
    input = "0";
}

input = Math.min(num_mozcoins - consumed_moz, parseInt(input));
current_txt.setString(input.toString());
current_capture.setString(input.toString());
current_branch.setAnimation(Math.floor(input / (Math.ceil(num_mozcoins / branch_num_animation))));

